mapper.insert:
        insert into 
        	p_cls_rep
	        	(meno,
	        	cno,
	        	titl,
	        	conts,
	        	star,
	        	phot,
	        	rgdt) 
        	values
        		(#{meno}
        		,#{cno}
        		,#{titl}
        		,#{conts}
        		,#{star}
        		,#{phot}
        		,now())

mapper.update:
update p_cls_rep
   set titl=#{titl},
   	   conts=#{conts},
   	   star=#{star},
   	   phot=#{phot}
 where rno=#{value}
